function e(){return{startY:0,currentY:0,moving:!1,init(){Alpine.effect(()=>{this.$el.parentElement.parentElement.style.transform=`translateY(${this.distance}px)`})},get distance(){return this.moving?Math.max(0,this.currentY-this.startY):0},handleMovingStart(t){this.moving=!0,this.startY=this.currentY=t.touches[0].clientY},handleWhileMoving(t){this.currentY=t.touches[0].clientY},handleMovingEnd(){this.distance>100&&Alpine.store("globalSearchModalStore").hideModal(),this.moving=!1}}}export{e as default};
